<?php

namespace App\Repository;

/**
 * ProviderRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProviderRepository extends \Doctrine\ORM\EntityRepository
{
    /**
     * Get Best Providers
     *
     * @return array
     */
    public function mostFans(int $limit){

        $qb = $this->_em->createQueryBuilder()
            ->select('p')
            ->from($this->_entityName,'p')
            ->innerJoin('p.logos','l')
            ->addSelect('l')
            ->orderBy('p.totalFans','DESC')
            ->setMaxResults($limit);

        return $qb->getQuery()->getResult();
    }

    /**
     * Get Recent Providers
     *
     * @return array
     */
    public function recentProviders(int $limit):array
    {
        $qb = $this->_em->createQueryBuilder()
            ->select('p')
            ->from($this->_entityName, 'p')
            ->orderBy('p.registryDate','DESC')
            ->setMaxResults($limit);

        return $qb->getQuery()->getResult();
    }
}
